import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchingClasses, setClasses, setError, classSelector } from "../../data-store/redux/classSlice";
import axios from "axios";
import Navbar from "../../components/navbar/navbar.jsx";
import { toast } from "react-toastify";
import "./home.css";

const Courses = () => {
  const dispatch = useDispatch();
  const { list: classes, loading, error } = useSelector(classSelector);

  useEffect(() => {
    const fetchClasses = async () => {
      dispatch(fetchingClasses());
      try {
        const response = await axios.get("http://localhost:3030/api/classes/classes");
        dispatch(setClasses(response.data));
        toast.success("Classes fetched successfully");
      } catch (error) {
        dispatch(setError("Classes not found"));
        toast.error("Classes not found");
      }
    };
    fetchClasses();
  }, [dispatch]);

  return (
    <div>
      <Navbar />
      <div className="classes-list">
        {loading ? (
          <p>Loading classes...</p>
        ) : error ? (
          <p>{error}</p>
        ) : classes.length > 0 ? (
          classes.map((classItem) => (
            <div key={classItem._id} className="class-item">
              <h3>{classItem.title}</h3>
              <p>{classItem.description}</p>
              <p>Instructor: {classItem.instructor.name}</p>
            </div>
          ))
        ) : (
          <p>No classes found</p>
        )}
      </div>
    </div>
  );
};

export default Courses;
