import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  fetchingClasses,
  setClasses,
  setError,
  classSelector,
} from "../../data-store/redux/classSlice";
import {
  enrollInClass,
  enrollSelector,
  fetchingEnroll,
  setEnrollSuccess,
  setEnrollError,
} from "../../data-store/redux/enrollSlice";
import axios from "axios";
import Navbar from "../../components/navbar/navbar.jsx";
import { toast } from "react-toastify";
import {
  Card,
  CardContent,
  Typography,
  Container,
  Box,
  Button,
} from "@mui/material";
import "./home.css";

const Courses = () => {
  const dispatch = useDispatch();
  const { list: classes, loading, error } = useSelector(classSelector);
  const { enrollLoading, enrollError, enrollSuccess } =
    useSelector(enrollSelector);

  useEffect(() => {
    const fetchClasses = async () => {
      dispatch(fetchingClasses());
      try {
        const response = await axios.get(
          "http://localhost:3030/api/classes/classes"
        );
        dispatch(setClasses(response.data));
        toast.success("Classes fetched successfully");
      } catch (error) {
        dispatch(setError("Classes not found"));
        toast.error("Classes not found");
      }
    };
    fetchClasses();
  }, [dispatch]);

  const handleEnroll = async (classId) => {
    dispatch(fetchingEnroll());
    try {
      const response = await axios.post(
        "http://localhost:3030/api/enrollments/enrollments",
        { classId }
      );
      dispatch(setEnrollSuccess(response.data));
      toast.success("Enrolled successfully");
    } catch (error) {
      dispatch(setEnrollError("Enrollment failed"));
      toast.error("Enrollment failed");
    }
  };

  return (
    <div>
      <Navbar />
      <Container className="classes-container">
        <div className="classes-header">
          <Typography variant="h4" gutterBottom>
            Available Courses
          </Typography>
          <Typography variant="h" gutterBottom>
            Enroll your Course
          </Typography>
        </div>
        <Box className="classes-list">
          {loading ? (
            <p>Loading classes...</p>
          ) : error ? (
            <p>{error}</p>
          ) : classes.length > 0 ? (
            classes.map((classItem, index) => (
              <Card
                key={classItem._id}
                className="class-item"
                sx={{
                  backgroundColor: index % 2 === 0 ? "#d1c4e9" : "#bbdefb",
                }}
              >
                <CardContent>
                  <Typography
                    gutterBottom
                    variant="h5"
                    component="div"
                    className="class-title"
                  >
                    {classItem.title}
                  </Typography>
                  <Typography
                    variant="body2"
                    color="text.secondary"
                    className="class-description"
                  >
                    {classItem.description}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Instructor: {classItem.instructor.name}
                  </Typography>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={() => handleEnroll(classItem._id)}
                    disabled={enrollLoading}
                    className="enroll-button"
                  >
                    Enroll
                  </Button>
                </CardContent>
              </Card>
            ))
          ) : (
            <p>No classes found</p>
          )}
        </Box>
      </Container>
    </div>
  );
};

export default Courses;
