import { createSlice  } from "@reduxjs/toolkit";

const initialState = {
  loading: false,
  list: [],
  selected: [],
  error: null,
};

const usersSlice = createSlice({
  name: "users",
  initialState,
  reducers: {
    fetchingUsers: (state) => {
      state.loading = true;
    },
    loadUsers: (state, action) => {
      const { payload } = action;
      state.loading = false;
      state.list = payload;
    },
    errorOccured: (state, action) => {
      const { payload } = action;
      state.loading = false;
      state.error = payload;
    },
    removeUser: (state, action) => {
      const { payload } = action;
      state.list = state.list.filter((u) => u.id !== payload);
    },
    selectUser: (state, action) => {
      const { payload } = action;
      const exists = state.selected.some((u) => u.id === payload);
      if (!exists) {
        state.selected.push(payload);
      }
    },
  },
});

export const {
  loadUsers,
  fetchingUsers,
  errorOccured,
  removeUser,
  selectUser,
} = usersSlice.actions;

export const usersSliceSelector = (global) => global.users;

export const usersReducer = usersSlice.reducer;

export default usersSlice;
