import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./register.css";
import { useDispatch, useSelector } from "react-redux";
import {
  registerUserSuccess,
  registerUserStart,
  registerUserFail,
  usersSliceSelector,
} from "../../data-store/redux/userSlice";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import axios from "axios";

const Register = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { loading, error, user } = useSelector(usersSliceSelector);

  const handleSubmit = async (e) => {
    e.preventDefault();
    dispatch(registerUserStart());
    try {
      const response = await axios.post(
        "http://localhost:3030/api/users/register",
        { name: username, email, password }
      );
      dispatch(registerUserSuccess(response.data));
    } catch (error) {
      dispatch(registerUserFail(error.response.data));
    }
  };

  useEffect(() => {
    if (user && !loading && !error) {
      navigate("/home");
      toast.success("Signup successful");
    } else if (error) {
      toast.error("Error signing up");
    }
  }, [user, loading, error, navigate]);

  return (
    <div className="register">
      <form className="register-form" onSubmit={handleSubmit}>
        <h2>Create an account</h2>
        <div className="form-group">
          <label>Name</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="What shall we call you?"
            required
          />
        </div>
        <div className="form-group">
          <label>Email address</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="you@domain.com"
            required
          />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Must be at least 8 characters"
            required
          />
        </div>
        <button type="submit" className="create-account">
          {loading ? "Registering..." : "Create account"}
        </button>
        <p>
          Been here before? <Link to="/login">Login</Link>
        </p>
      </form>
    </div>
  );
};

export default Register;
