import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  fetchingClasses,
  setClasses,
  setError,
  classSelector,
} from "../../data-store/redux/classSlice";
import axios from "axios";
import Navbar from "../../components/navbar/navbar.jsx";
import { toast } from "react-toastify";
import { Card, CardContent, Typography, Container, Box } from "@mui/material";
import "./home.css";

const Courses = () => {
  const dispatch = useDispatch();
  const { list: classes, loading, error } = useSelector(classSelector);

  useEffect(() => {
    const fetchClasses = async () => {
      dispatch(fetchingClasses());
      try {
        const response = await axios.get(
          "http://localhost:3030/api/classes/classes"
        );
        dispatch(setClasses(response.data));
        toast.success("Classes fetched successfully");
      } catch (error) {
        dispatch(setError("Classes not found"));
        toast.error("Classes not found");
      }
    };
    fetchClasses();
  }, [dispatch]);

  return (
    <div style={{display:flex , f}}>
      <Navbar />
      <Container style={{ marginTop: "20px" }}>
        <div className="classes-list">
          {loading ? (
            <p>Loading classes...</p>
          ) : error ? (
            <p>{error}</p>
          ) : classes.length > 0 ? (
            <Box
              display="flex"
              flexDirection="row"
              flexWrap="wrap"
              gap={2}
              justifyContent="space-between"
            >
              {classes.map((classItem) => (
                <Card
                  key={classItem._id}
                  className="class-item"
                  style={{
                    flex: "1 1 30%",
                    minWidth: "300px",
                    marginBottom: "20px",
                  }}
                >
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                      {classItem.title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {classItem.description}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Instructor: {classItem.instructor.name}
                    </Typography>
                  </CardContent>
                </Card>
              ))}
            </Box>
          ) : (
            <p>No classes found</p>
          )}
        </div>
      </Container>
    </div>
  );
};

export default Courses;
