import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  fetchingClasses,
  setClasses,
  setError,
  classSelector,
} from "../../data-store/redux/classSlice";
import axios from "axios";
import Navbar from "../../components/navbar/navbar.jsx";
import { toast } from "react-toastify";
import {
  Card,
  CardContent,
  CardMedia,
  Typography,
  Grid,
  Container,
} from "@mui/material";
import "./home.css";

const Courses = () => {
  const dispatch = useDispatch();
  const { list: classes, loading, error } = useSelector(classSelector);

  useEffect(() => {
    const fetchClasses = async () => {
      dispatch(fetchingClasses());
      try {
        const response = await axios.get(
          "http://localhost:3030/api/classes/classes"
        );
        dispatch(setClasses(response.data));
        toast.success("Classes fetched successfully");
      } catch (error) {
        dispatch(setError("Classes not found"));
        toast.error("Classes not found");
      }
    };
    fetchClasses();
  }, [dispatch]);

  return (
    <div>
      <Navbar />
      <Container>
        <div className="classes-list">
          {loading ? (
            <p>Loading classes...</p>
          ) : error ? (
            <p>{error}</p>
          ) : classes.length > 0 ? (
            <Grid container spacing={4}>
              {classes.map((classItem) => (
                <Grid item key={classItem._id} xs={12} sm={6} md={4}>
                  <Card className="class-item">
                    <CardContent>
                      <Typography gutterBottom variant="h5" component="div">
                        {classItem.title}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {classItem.description}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Instructor: {classItem.instructor.name}
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          ) : (
            <p>No classes found</p>
          )}
        </div>
      </Container>
    </div>
  );
};

export default Courses;
