import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  fetchingClasses,
  setClasses,
  setError,
  classSelector,
} from "../../data-store/redux/classSlice";
import axios from "axios";
import Navbar from "../../components/navbar/navbar.jsx";
import { toast } from "react-toastify";
import {
  Card,
  CardContent,
  Typography,
  Container,
  Box,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Button,
} from "@mui/material";
import "./home.css";

const Courses = () => {
  const dispatch = useDispatch();
  const { list: classes, loading, error } = useSelector(classSelector);
  const [selectedClass, setSelectedClass] = useState(null);

  useEffect(() => {
    const fetchClasses = async () => {
      dispatch(fetchingClasses());
      try {
        const response = await axios.get(
          "http://localhost:3030/api/classes/classes"
        );
        dispatch(setClasses(response.data));
        toast.success("Classes fetched successfully");
      } catch (error) {
        dispatch(setError("Classes not found"));
        toast.error("Classes not found");
      }
    };
    fetchClasses();
  }, [dispatch]);

  const handleCardClick = (classItem) => {
    setSelectedClass(classItem);
  };

  const handleClose = () => {
    setSelectedClass(null);
  };

  return (
    <div>
      <Navbar />
      <Container>
        <div className="classes-list">
          {loading ? (
            <p>Loading classes...</p>
          ) : error ? (
            <p>{error}</p>
          ) : classes.length > 0 ? (
            <Box
              display="flex"
              flexDirection="row"
              flexWrap="wrap"
              gap={2}
              justifyContent="space-between"
            >
              {classes.map((classItem) => (
                <Card
                  key={classItem._id}
                  className="class-item"
                  style={{
                    flex: "1 1 30%",
                    minWidth: "300px",
                    marginBottom: "20px",
                    cursor: "pointer",
                  }}
                  onClick={() => handleCardClick(classItem)}
                >
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                      {classItem.title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {classItem.description}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Instructor: {classItem.instructor.name}
                    </Typography>
                  </CardContent>
                </Card>
              ))}
            </Box>
          ) : (
            <p>No classes found</p>
          )}
        </div>
      </Container>

      {selectedClass && (
        <Dialog open={Boolean(selectedClass)} onClose={handleClose}>
          <DialogTitle>{selectedClass.title}</DialogTitle>
          <DialogContent>
            <DialogContentText>{selectedClass.description}</DialogContentText>
            <DialogContentText>
              Instructor: {selectedClass.instructor.name}
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose} color="primary">
              Close
            </Button>
          </DialogActions>
        </Dialog>
      )}
    </div>
  );
};

export default Courses;
